    	//$page_url = 'http://www.hkexnews.hk/listedco/listconews/advancedsearch/search_active_main.aspx';
    	//$viewstate = '';
    	//$stock_code = '00005';

    	$page_url = 'http://www.hkexnews.hk/listedco/listconews/advancedsearch/search_active_main_c.aspx';

    	$stock_code = $_GET["code"];
    	$stock_date = '20150321';
    	$hfStatus = 'ACM';
		$dateRange = 'Year';
    	$viewstateencrypted = '';
        $viewstate = '';
		
		/*		
		$fields = array(
						'ctl00$txt_stock_code' => urlencode($stock_code),
						'ctl00_txt_today' => urlencode($stock_date),
						'ctl00_hfStatus' => urlencode($hfStatus),
