						'__VIEWSTATEENCRYPTED' => urlencode($viewstateencrypted),
						'__VIEWSTATE' => urlencode($viewstate)
		);*/
		
			
		$fields = array(
						'ctl00$txt_stock_code' => $stock_code,
						'ctl00_txt_today' => $stock_date,
						'ctl00_hfStatus' => $hfStatus,
						'ctl00$sel_defaultDateRange' => $dateRange,
						'__VIEWSTATEENCRYPTED' => $viewstateencrypted,
						'__VIEWSTATE' => urlencode($viewstate)
		);
				
		foreach($fields as $key=>$value) { $fields_string .= $key.'='.$value.'&'; }
		rtrim($fields_string, '&');
				
		$cPage = $this->get_data($page_url,'POST', $fields_string);
		echo $cPage;
    }

	private function get_data($url, $method, $string) {
		$ch = curl_init();
		$timeout = 5;
		curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
		curl_setopt($ch, CURLOPT_VERBOSE, 1);
		curl_setopt($ch, CURLOPT_HEADER, 1);

		curl_setopt($ch, CURLOPT_URL, $url);
		curl_setopt($ch, CURLOPT_POST, count($fields));
		curl_setopt($ch, CURLOPT_POSTFIELDS, $string);
		curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
		curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
		curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, $timeout);
		$data = curl_exec($ch);
		
		$header_size = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
		$header = substr($data, 0, $header_size);
		$body = substr($data, $header_size);
		
		curl_close($ch);
		return $data;
	}

}

